import { Component } from 'react';
import Home from '../components/Home';
import Search from '../components/Search';
import Favourites from '../components/Favourites';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'
import MediaCard from './MediaCard';


function Container(props){
        let location = useLocation()
        return (
            <div className="container">
                <Route path="/" exact render={() => <Home data={props.data} location={location.pathname}/>} />
                <Route path="/search" exact render={() => <Search  location={location.pathname}/>} />
                <Route path="/favourites" exact render={() => <Favourites />} />
                <Route path='/favourite/:id' exact render={({ match }) => <Favourites match={match}  />} />

=            </div>
        )
}


export default Container;
