import { Component, useState, useEffect } from 'react';
import MediaCard from './MediaCard';
import axios from 'axios';

function Favourites(props) {
    const [favourites, setFavourites] = useState([])

    const save = async (img) => {
        const response = await axios.post('http://localhost:4200/image', img)
        console.log(response.data)
        setFavourites([...favourites, response.data ])
    }

    const saveToFavourites = (img) => {
        save(img)
    }

    return (
        <div>
            {props.favourites.map(f => <MediaCard  favourite={f} saveToFavourites={saveToFavourites} />)}
        </div>


    )
}

export default Favourites;
