import { useState } from 'react';
import '../styles//mediaCard.css';
import Media from './Media';
import Favourites from './Favourites';
import axios from 'axios';

function MediaCard(props) {
    const [favourites, setFavourites] = useState([])


    const save = async (img) => {
        const response = await axios.post('http://localhost:4200/image', img)
        const tempArr = [...favourites]
        tempArr.push(response.data)
        setFavourites(tempArr)
    }

    const saveToFavourites = (img) => {
        save(img)
    }

    return (
        <div className="MediaCard">
            {props.response != undefined ?
                <div className="homePage">
                    <div className="title">{props.data.title}</div>
                    <div className="img"><iframe width="820" height="515" src={props.data.url}></iframe></div>
                    <div className="desc">{props.data.explanation}</div>
                </div>
                : null}

            {/* {props.data != undefined ?
                <div className="favouritesPage">
                    {props.data.map(p => <Media image={p} saveToFavourites={saveToFavourites} />)}
                </div> : null}

            {favourites != undefined ?
                <div className="favourites">
                    {favourites.map(f => <Favourites fav={f}/>)}
                </div> : null} */}


        </div>
    )
}

export default MediaCard;
