import { Component, useEffect, useState } from 'react';
import '../styles//media.css';



function Media(props) {
    const image = props.image

    const saveToFavourites =  () => {
        img = {title: image.data[0].title, }
        props.saveToFavourites(img)
    }

    return (

        <div className="Media"> 

        <div className="subTitle">{image.data[0].title}</div>
        <div className="img"><img width="420" height="315" src={image.links[0].href}></img></div>
        <i class="fas fa-thumbs-up" id="likes" onClick={saveToFavourites}></i>
        {/* <i class="fas fa-thumbs-down" id="dislikes"></i> */}


        </div>
    )
}

export default Media;
