import { Component } from 'react';
import '../styles//media.css';



function Media(props) {
    const [image, setImage] = useState({title: "", imgUrl: "", description: ""})

    const saveToFavourites = () => {
        return props.saveToFavourites(image)       
    }
    return (

        <div className="Media">
            {props.data.length != 0  ? 
            <div>

                <br/>
                <br/>
                <br/>
                <br/>

            {props.data.map(p => <div id="media">
                <div className="subTitle">{p.data[0].title}</div>
                <div className="img"><img width="420" height="315" src={p.links[0].href}></img></div>
                {setImage(

                )}
                <i class="fas fa-thumbs-up" id="likes" onClick={saveToFavourites}></i>
                {/* <i class="fas fa-thumbs-down" id="dislikes"></i> */}
             </div>)}
            </div>
            : null}

        </div>
    )
}

export default Media;
