import { Component, useState, useEffect, useLocation } from 'react';
import MediaCard from './MediaCard';
import axios from 'axios';

function Favourites() {
    let location = useLocation()
    const [favourites, setFavourites] = useState([])

    const getTransactions = async () => {
        return axios.get('http://localhost:4200/images')
    }

    useEffect(async () => {
        const response = await getTransactions()
        setFavourites(response.data)
    }, [favourites])


    const removeFromFavourites = async (id) => {
        await axios.delete(`http://localhost:4200/image/${id}`)
        const index = favourites.findIndex(d => d._id === id)
        if (index !== -1) favourites.splice(index, 1);
    }

    return (
        <div>
            {favourites.map(f => <MediaCard favourite={f} removeFromFavourites={removeFromFavourites} location={location.pathname}/>)}
        </div>

    )
}

export default Favourites;
